import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import AppNavbar from './AppNavbar';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';
import Select, { SelectChangeEvent } from '@mui/material/Select';

class VehicleEdit extends Component {

    emptyItem = {
        taskId: '',
        description: '',
        dateEntered: null,
        dateDue: null,
        instructions: '',
        notes: '',
        type: '',
        vehicle: ''
        };

    constructor(props) {
        super(props);
        this.state = {
            item: this.emptyItem
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
        this.getVehicles = this.getVehicles.bind(this);
    }

    async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            const task = await (await fetch(`/api/tasks/${this.props.match.params.id}`)).json();
            this.setState({item: task});
        }
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let item = {...this.state.item};
        item[name] = value;
        this.setState({item});
    }

    async handleSubmit(event) {
        event.preventDefault();
        const {item} = this.state;
        console.log(JSON.stringify(item));
        await fetch('/api/tasks' + (item.taskId ? '/' + item.taskId : ''), {
            method: (item.taskId) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(item),
        });
        this.props.history.push('/tasks');
    }

    render() {
        const {item} = this.state;
        const title = <h2>{item.taskId ? 'Edit Task' : 'Add Task'}</h2>;
    
        return <div>
            <AppNavbar/>
            <Container maxWidth="sm">
                {title}
                <form onSubmit={this.handleSubmit}>
                    <Stack direction="column" spacing={1}>
                        <TextField label="Task ID" variant="outlined" name="taskId" id="taskId" value={item.taskId || ''}
                            disabled={true} sx={{visibility: item.taskId ? "visible" : "hidden"}}/>
                        <TextField label="Description" variant="outlined" name="description" id="description" value={item.description || ''}
                                onChange={this.handleChange} />
                        {/*<TextField label="Vehicle ID" variant="outlined" name="vehicleId" id="vehicleId" value={item.vehicle.vehicleId || ''}
                                onChange={this.handleChange} />*/}
                        <Select variant="outlined" id="vehicle-select" value={item.vehicle.vehicleId + ': ' + item.vehicle.description}
                         label="Vehicle" onChange={this.handleChange}/>
                         {this.getVehicles()}
                        <TextField label="Task Type" variant="outlined" name="taskType" id="taskType" value={item.type.typeId || ''}
                                onChange={this.handleChange} />
                        <TextField label="Date Entered" variant="outlined" name="dateEntered" id="dateEntered" value={item.dateEntered || ''}
                                onChange={this.handleChange} />
                        <TextField label="Date Due" variant="outlined" name="dateDue" id="dateDue" value={item.dateDue || ''}
                                onChange={this.handleChange} />
                        <TextField label="Instructions" variant="outlined" name="instructions" id="instructions" value={item.instructions || ''}
                                onChange={this.handleChange} />
                        <TextField label="Notes" variant="outlined" name="notes" id="notes" value={item.notes || ''}
                                onChange={this.handleChange} />
                        <Stack direction="row" spacing={1}>
                            <Button color="primary" variant="outlined" type="submit">Save</Button>{' '}
                            <Button color="secondary" variant="outlined" component={Link} to="/tasks">Cancel</Button>
                        </Stack>
                    </Stack>
                </form>
            </Container>
        </div>
    }
}
export default withRouter(VehicleEdit);
