import React, { Component } from 'react';
import { Link, withRouter } from 'react-router-dom';
import AppNavbar from './AppNavbar';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Stack from '@mui/material/Stack';
import Container from '@mui/material/Container';

class EmployeeEdit extends Component {

    emptyItem = {
        name: '',
        email: '',
        dob: ''
    };

    constructor(props) {
        super(props);
        this.state = {
            item: this.emptyItem
        };
        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    async componentDidMount() {
        if (this.props.match.params.id !== 'new') {
            const employee = await (await fetch(`/employees/${this.props.match.params.id}`)).json();
            this.setState({item: employee});
        }
    }

    handleChange(event) {
        const target = event.target;
        const value = target.value;
        const name = target.name;
        let item = {...this.state.item};
        item[name] = value;
        this.setState({item});
    }

    async handleSubmit(event) {
        event.preventDefault();
        const {item} = this.state;
        console.log(JSON.stringify(item));
        await fetch('/employees' + (item.id ? '/' + item.id : ''), {
            method: (item.id) ? 'PUT' : 'POST',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(item),
        });
        this.props.history.push('/employees');
    }

    render() {
        const {item} = this.state;
        const title = <h2>{item.id ? 'Edit Employee' : 'Add Employee'}</h2>;
    
        return <div>
            <AppNavbar/>
            <Container maxWidth="sm">
                {title}
                <form onSubmit={this.handleSubmit}>
                    <Stack direction="column" spacing={1}>
                        <TextField label="Name" variant="outlined" name="name" id="name" value={item.name || ''}
                                onChange={this.handleChange} />
                        <TextField label="Email" variant="outlined" name="email" id="email" value={item.email || ''}
                                onChange={this.handleChange} />
                        <TextField label="Date Of Birth" variant="outlined" name="dob" id="dob" value={item.dob || ''}
                                onChange={this.handleChange} />
                        <Stack direction="row" spacing={1}>
                            <Button color="primary" variant="outlined" type="submit">Save</Button>{' '}
                            <Button color="secondary" variant="outlined" component={Link} to="/employees">Cancel</Button>
                        </Stack>
                    </Stack>
                </form>
            </Container>
        </div>
    }
}
export default withRouter(EmployeeEdit);
