import React, { Component } from 'react';
import { Stack, ButtonGroup, Container, TableContainer, 
        Table, TableHead, TableRow, TableCell, TableBody, 
        IconButton, Dialog, DialogTitle, DialogContentText,
        DialogContent, DialogActions, Button } from '@mui/material';
import ModeEditIcon from '@mui/icons-material/ModeEdit';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import PersonAddAlt1Icon from '@mui/icons-material/PersonAddAlt1';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

class EmployeeList extends Component {

    constructor(props) {
        super(props);
        this.state = {
                employees: [],
                deleteDialogOpen: false,
                employeeToDelete: null
            };
        this.remove = this.remove.bind(this);
        this.handleDialogClose = this.handleDialogClose.bind(this);
        this.handleDelete = this.handleDelete.bind(this);
    }

    componentDidMount() {
        fetch('/employees')
            .then(response => response.json())
            .then(data => this.setState({employees: data}));
    }

    async remove(id) {
        await fetch(`/employees/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedEmployees = [...this.state.employees].filter(i => i.id !== id);
            this.setState({employees: updatedEmployees});
        });
    }

    handleDialogClose(choice) {
        if(choice === 'DELETE'){
            this.remove(this.state.employeeToDelete.id)
        }
        this.setState({
            deleteDialogOpen: false
        });   
    }

    handleDelete(employeeId) {
        this.setState({
            employeeToDelete: employeeId,
            deleteDialogOpen: true
        })
        console.log(this.state);
    }
    
    render() {
        const {employees, isLoading} = this.state;
    
        if (isLoading) {
            return <p>Loading...</p>;
        }
    
        const employeeList = employees.map(employee => {
            return (
                <TableRow key={employee.id}>
                    <TableCell style={{whiteSpace: 'nowrap'}}>{employee.name}</TableCell>
                    <TableCell>{employee.email}</TableCell>
                    <TableCell>
                        <ButtonGroup>
                            <IconButton color="success" component={Link} to={"/employees/" + employee.id}>
                                <ModeEditIcon/>
                            </IconButton>
                            <IconButton color="error" onClick={() => this.handleDelete(employee)}>
                                <DeleteForeverIcon/>
                            </IconButton>
                        </ButtonGroup>
                    </TableCell>
                </TableRow>
            )
        });
    
        return (
            <div>
                <AppNavbar/>
                <Container fluid>
                    <Stack direction="row">
                        <h3>Employees</h3>
                        <IconButton color="success" component={Link} to="/employees/new">
                            <PersonAddAlt1Icon/>
                        </IconButton>
                    </Stack>
                    <TableContainer>
                        <Table className="mt-4">
                            <TableHead>
                            <TableRow>
                                <TableCell width="30%">Name</TableCell>
                                <TableCell width="30%">Email</TableCell>
                                <TableCell width="40%">Actions</TableCell>
                            </TableRow>
                            </TableHead>
                            <TableBody>
                                {employeeList}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Container>
                <Dialog
                    open={this.state.deleteDialogOpen}
                    aria-labelledby="alert-dialog-title"
                    aria-describedby="alert-dialog-description"
                >
                    <DialogTitle id="alert-dialog-title">
                    {"Are you sure you want to delete this employee?"}
                    </DialogTitle>
                    <DialogContent>
                    <DialogContentText id="alert-dialog-description">
                        {this.state.deleteDialogOpen ? JSON.stringify(this.state.employeeToDelete) : ''}
                    </DialogContentText>
                    </DialogContent>
                    <DialogActions>
                    <Button onClick={() => this.handleDialogClose('CANCEL')}>Cancel</Button>
                    <Button onClick={() => this.handleDialogClose('DELETE')} autoFocus>
                        Delete
                    </Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }
}
export default EmployeeList;
