import React, { Component } from "react";
import { Link, withRouter } from "react-router-dom";
import AppNavbar from "./AppNavbar";
import {
  TextField,
  Button,
  Container,
  Stack,
  List,
  ListItem,
  ListItemButton,
  ListItemIcon,
  ListItemText,
  TableContainer,
  Table,
  TableHead,
  TableRow,
  TableCell,
  TableBody,
  ButtonGroup,
  IconButton,
} from "@mui/material";
import AccessTimeIcon from "@mui/icons-material/AccessTime";
import TextFieldsIcon from "@mui/icons-material/TextFields";
import ModeEditIcon from "@mui/icons-material/ModeEdit";
import DeleteForeverIcon from "@mui/icons-material/DeleteForever";

class Options extends Component {
  constructor(props) {
    super(props);
    this.state = {
      timeUnits: [],
      taskTypes: [],
      currentObjectSelection: "task-types",
      editorOpen: false,
      currentValue: "",
    };
    this.handleClick = this.handleClick.bind(this);
    this.openEditor = this.openEditor.bind(this);
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  async componentDidMount() {
    fetch("/api/time-units")
      .then((response) => response.json())
      .then((data) => this.setState({ timeUnits: data }));
    fetch("/api/task-types")
      .then((response) => response.json())
      .then((data) => this.setState({ taskTypes: data }));
  }

  handleClick(event) {
    let selection = event.currentTarget.getAttribute("name");
    this.setState({
      currentObjectSelection: selection,
    });
  }

  openEditor(value) {
    this.setState({
      editorOpen: true,
      currentValue: value,
    });
  }

  handleChange(event) {
    const target = event.target;
    const value = target.value;
    const name = target.name;
    let item = { ...this.state.item };
    item[name] = value;
    this.setState({ item });
  }

  async handleSubmit(event) {
    /* TODO:  Rewrite for task types and time units */
    event.preventDefault();
    const { item } = this.state;
    await fetch("/api/tasks" + (item.taskId ? "/" + item.taskId : ""), {
      method: item.taskId ? "PUT" : "POST",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(item),
    });
    this.props.history.push("/tasks");
  }

  render() {
    const title = <h2>Options</h2>;
    const detailHeader = (
      <h2>
        {this.state.currentObjectSelection
          .replace("-", " ")
          .replace(/(^\w{1})|(\s\w{1})/g, (l) => l.toUpperCase())}
      </h2>
    );
    const valueType = this.state.currentObjectSelection
      .replace(/(-\w{1})/g, (l) => l.toUpperCase())
      .replace("-", "");
    const values = this.state[valueType];
    const valueId = () => {
      switch (this.state.currentObjectSelection) {
        case "task-types":
          return "typeId";
        case "time-units":
          return "unitId";
        default:
          return "typeId";
      }
    };
    const valueList = values.map((value) => {
      return (
        <TableRow key={value[valueId()]}>
          <TableCell style={{ whiteSpace: "nowrap" }}>{value.value}</TableCell>
          <TableCell>{value.description}</TableCell>
          <TableCell>
            <ButtonGroup>
              <IconButton color="success" onclick={this.openEditor}>
                <ModeEditIcon />
              </IconButton>
              <IconButton
                color="error"
                onClick={() => this.handleDelete(value)}
              >
                <DeleteForeverIcon />
              </IconButton>
            </ButtonGroup>
          </TableCell>
        </TableRow>
      );
    });

    return (
      <div>
        <AppNavbar />
        <Container maxWidth="lg">
          {title}
          <Stack direction="row">
            <Container maxWidth="xs">
              <List>
                <ListItem
                  name="task-types"
                  disablePadding
                  divider={true}
                  selected={this.state.currentObjectSelection === "task-types"}
                  onClick={this.handleClick}
                >
                  <ListItemButton>
                    <ListItemIcon>
                      <TextFieldsIcon />
                    </ListItemIcon>
                    <ListItemText primary="Task Types" />
                  </ListItemButton>
                </ListItem>
                <ListItem
                  disablePadding
                  divider={true}
                  selected={this.state.currentObjectSelection === "time-units"}
                >
                  <ListItemButton name="time-units" onClick={this.handleClick}>
                    <ListItemIcon>
                      <AccessTimeIcon />
                    </ListItemIcon>
                    <ListItemText primary="Time Units" />
                  </ListItemButton>
                </ListItem>
              </List>
            </Container>
            <Container maxWidth="md">
              {detailHeader}
              <TableContainer>
                <Table className="mt-4">
                  <TableHead>
                    <TableRow>
                      <TableCell width="40%">
                        <b>Value</b>
                      </TableCell>
                      <TableCell width="50%">
                        <b>Description</b>
                      </TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>{valueList}</TableBody>
                </Table>
              </TableContainer>
            </Container>
          </Stack>
        </Container>
      </div>
    );
  }
}
export default withRouter(Options);
