import React, { Component } from 'react';
import { Stack, ButtonGroup, Container, TableContainer, Table, TableHead, TableRow, TableCell, TableBody, IconButton } from '@mui/material';
import ModeEditIcon from '@mui/icons-material/ModeEdit';
import DeleteForeverIcon from '@mui/icons-material/DeleteForever';
import PersonAddAlt1Icon from '@mui/icons-material/PersonAddAlt1';
import AppNavbar from './AppNavbar';
import { Link } from 'react-router-dom';

class EmployeeList extends Component {

    constructor(props) {
        super(props);
        this.state = {employees: []};
        this.remove = this.remove.bind(this);
    }

    componentDidMount() {
        fetch('/employees')
            .then(response => response.json())
            .then(data => this.setState({employees: data}));
    }

    async remove(id) {
        await fetch(`/employees/${id}`, {
            method: 'DELETE',
            headers: {
                'Accept': 'application/json',
                'Content-Type': 'application/json'
            }
        }).then(() => {
            let updatedEmployees = [...this.state.employees].filter(i => i.id !== id);
            this.setState({employees: updatedEmployees});
        });
    }
    
    render() {
        const {employees, isLoading} = this.state;
    
        if (isLoading) {
            return <p>Loading...</p>;
        }
    
        const employeeList = employees.map(employee => {
            return (
                <TableRow key={employee.id}>
                    <TableCell style={{whiteSpace: 'nowrap'}}>{employee.name}</TableCell>
                    <TableCell>{employee.email}</TableCell>
                    <TableCell>
                        <ButtonGroup>
                            <IconButton color="success" component={Link} to={"/employees/" + employee.id}>
                                <ModeEditIcon/>
                            </IconButton>
                            <IconButton color="error" onClick={() => this.remove(employee.id)}>
                                <DeleteForeverIcon/>
                            </IconButton>
                        </ButtonGroup>
                    </TableCell>
                </TableRow>
            )
        });
    
        return (
            <div>
                <AppNavbar/>
                <Container fluid>
                    <Stack direction="row">
                        <h3>Employees</h3>
                        <IconButton color="success" component={Link} to="/employees/new">
                            <PersonAddAlt1Icon/>
                        </IconButton>
                    </Stack>
                    <TableContainer>
                        <Table className="mt-4">
                            <TableHead>
                            <TableRow>
                                <TableCell width="30%">Name</TableCell>
                                <TableCell width="30%">Email</TableCell>
                                <TableCell width="40%">Actions</TableCell>
                            </TableRow>
                            </TableHead>
                            <TableBody>
                                {employeeList}
                            </TableBody>
                        </Table>
                    </TableContainer>
                </Container>
            </div>
        );
    }
}
export default EmployeeList;
